import React, { useState } from 'react';
import './App.css'; // Import CSS file for styling

function App() {
  // Sample restaurant data
  const restaurants = [
    { name: 'Restaurant A', location: 'Atlanta', cuisine: 'Italian', specials: 'Pasta Night', website: 'https://restaurantA.com' },
    { name: 'Restaurant B', location: 'Atlanta', cuisine: 'Mexican', specials: 'Taco Tuesday', website: 'https://restaurantB.com' },
    { name: 'Restaurant C', location: 'Atlanta', cuisine: 'American', specials: 'Burger Special', website: 'https://restaurantC.com' },
    // Add more restaurant objects as needed
  ];

  // Options for location filter dropdown
  const locationOptions = ['All', 'Atlanta', 'New York', 'Los Angeles', 'Chicago']; // Sample options

  // Options for cuisine filter dropdown
  const cuisineOptions = ['All', 'Italian', 'Mexican', 'American', 'Japanese']; // Sample options

  // States for filters
  const [locationFilter, setLocationFilter] = useState('All');
  const [cuisineFilter, setCuisineFilter] = useState('All');

  // Filtered restaurants based on location and cuisine
  const filteredRestaurants = restaurants.filter((restaurant) => {
    return (
      (locationFilter === 'All' || restaurant.location === locationFilter) &&
      (cuisineFilter === 'All' || restaurant.cuisine === cuisineFilter)
    );
  });

  return (
    <div className="container">
      <h1 className="title">Atlanta Specials</h1>
      <div className="filters">
        <label>Location Filter:</label>
        <select value={locationFilter} onChange={(e) => setLocationFilter(e.target.value)}>
          {locationOptions.map((option, index) => (
            <option key={index} value={option}>{option}</option>
          ))}
        </select>
        <label>Cuisine Type Filter:</label>
        <select value={cuisineFilter} onChange={(e) => setCuisineFilter(e.target.value)}>
          {cuisineOptions.map((option, index) => (
            <option key={index} value={option}>{option}</option>
          ))}
        </select>
      </div>
      <div className="table-container">
        <table className="restaurant-table">
          <thead>
            <tr>
              <th>Restaurant Name</th>
              <th>Location</th>
              <th>Cuisine Type</th>
              <th>Specials</th>
              <th>Website Link</th>
            </tr>
          </thead>
          <tbody>
            {filteredRestaurants.map((restaurant, index) => (
              <tr key={index}>
                <td>{restaurant.name}</td>
                <td>{restaurant.location}</td>
                <td>{restaurant.cuisine}</td>
                <td>{restaurant.specials}</td>
                <td><a href={restaurant.website} target="_blank" rel="noopener noreferrer">{restaurant.website}</a></td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
