import React, { useState } from 'react';

function App() {
  // Sample restaurant data
  const restaurants = [
    { name: 'Restaurant A', location: 'New York', cuisine: 'Italian' },
    { name: 'Restaurant B', location: 'Los Angeles', cuisine: 'Mexican' },
    { name: 'Restaurant C', location: 'Chicago', cuisine: 'American' },
    // Add more restaurant objects as needed
  ];

  // States for filters
  const [locationFilter, setLocationFilter] = useState('');
  const [cuisineFilter, setCuisineFilter] = useState('');

  // Filtered restaurants based on location and cuisine
  const filteredRestaurants = restaurants.filter((restaurant) => {
    return (
      restaurant.location.toLowerCase().includes(locationFilter.toLowerCase()) &&
      restaurant.cuisine.toLowerCase().includes(cuisineFilter.toLowerCase())
    );
  });

  return (
    <div>
      <h1>Restaurant List</h1>
      <div>
        <label>Location Filter:</label>
        <input
          type="text"
          value={locationFilter}
          onChange={(e) => setLocationFilter(e.target.value)}
        />
      </div>
      <div>
        <label>Cuisine Type Filter:</label>
        <input
          type="text"
          value={cuisineFilter}
          onChange={(e) => setCuisineFilter(e.target.value)}
        />
      </div>
      <table>
        <thead>
          <tr>
            <th>Restaurant Name</th>
            <th>Location</th>
            <th>Cuisine Type</th>
          </tr>
        </thead>
        <tbody>
          {filteredRestaurants.map((restaurant, index) => (
            <tr key={index}>
              <td>{restaurant.name}</td>
              <td>{restaurant.location}</td>
              <td>{restaurant.cuisine}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
