import React, { useState } from 'react';
import './App.css'; // Import CSS file for styling

function App() {
  // Sample restaurant data
  const restaurants = [
    { name: 'Restaurant A', location: 'Atlanta', cuisine: 'Italian', specials: 'Pasta Night' },
    { name: 'Restaurant B', location: 'Atlanta', cuisine: 'Mexican', specials: 'Taco Tuesday' },
    { name: 'Restaurant C', location: 'Atlanta', cuisine: 'American', specials: 'Burger Special' },
    // Add more restaurant objects as needed
  ];

  // States for filters
  const [locationFilter, setLocationFilter] = useState('');
  const [cuisineFilter, setCuisineFilter] = useState('');

  // Filtered restaurants based on location and cuisine
  const filteredRestaurants = restaurants.filter((restaurant) => {
    return (
      restaurant.location.toLowerCase().includes(locationFilter.toLowerCase()) &&
      restaurant.cuisine.toLowerCase().includes(cuisineFilter.toLowerCase())
    );
  });

  return (
    <div className="container">
      <h1 className="title">Atlanta Specials</h1>
      <div className="filters">
        <label>Location Filter:</label>
        <input
          type="text"
          value={locationFilter}
          onChange={(e) => setLocationFilter(e.target.value)}
        />
        <label>Cuisine Type Filter:</label>
        <input
          type="text"
          value={cuisineFilter}
          onChange={(e) => setCuisineFilter(e.target.value)}
        />
      </div>
      <div className="table-container">
        <table className="restaurant-table">
          <thead>
            <tr>
              <th>Restaurant Name</th>
              <th>Location</th>
              <th>Cuisine Type</th>
              <th>Specials</th> {/* New column for specials */}
            </tr>
          </thead>
          <tbody>
            {filteredRestaurants.map((restaurant, index) => (
              <tr key={index}>
                <td>{restaurant.name}</td>
                <td>{restaurant.location}</td>
                <td>{restaurant.cuisine}</td>
                <td>{restaurant.specials}</td> {/* Display specials */}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
