import fs from 'fs'
import Papa from 'papaparse';

// Function to create a restaurant object
function createRestaurant(name, locations, cuisine, specials, website) {
    return {
        name: name,
        location: locations,
        cuisine: cuisine,
        specials: specials,
        website: website
    };
}

export const restaurantsList = [];

fs.createReadStream('tests.csv')
  .pipe(Papa.parse(Papa.NODE_STREAM_INPUT, { header: true })) // Assuming headers are present in the CSV
  .on('data', (row) => {
    // Parse each row and create a restaurant object
    const specials = {};
    // Parse specials for each day of the week
    for (const day of Object.keys(row.data)) {
      if (day !== 'name' && day !== 'location' && day !== 'cuisine' && day !== 'Website') {
        specials[day] = row.data[day] || ''; // Assign empty string if no special for that day
      }
    }
    const restaurant = createRestaurant(
      row.data.name,
      row.data.location.split(',').map(loc => loc.trim()), // Assuming locations are comma-separated
      row.data.cuisine,
      specials,
      row.data.Website
    );
    restaurantsList.push(restaurant);
  })
  .on('end', () => {
    console.log(restaurantsList);
    // Do whatever you need to do with the restaurantsList array here
  });

// Static properties
export const daysOfWeek = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];

const allLocations = restaurantsList.reduce((locations, restaurant) => {
    restaurant.location.forEach(location => {
        if (!locations.includes(location)) {
            locations.push(location);
        }
    });
    return locations;
}, []);

export const locationOptions = ['All', ...allLocations];
export const cuisineOptions = ['All', 'Italian', 'Mexican', 'American', 'Japanese'];
